import React, { useState, useEffect } from 'react';
import Video from './Video';
import { searchVideos } from './VideoList';

export default function VideoPage() {
    const [videos, setVideos] = useState([]);
    const [query, setQuery] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const fetchVideos = async (searchQuery) => {
        setLoading(true);
        setError('');
        try {
            const results = await searchVideos(searchQuery);
            setVideos(results);
        } catch (err) {
            setError('Erro ao carregar vídeos.');
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchVideos('Como investir em ações imobiliárias');
    }, []);

    const handleSearch = (event) => {
        event.preventDefault();
        if (query.trim()) {
            fetchVideos(query);
        }
    };

    return (
        <div className="video-page">
            <h1 className="video-page__title">Vídeos Recomendados</h1>

            {/* Formulário de Busca */}
            <form onSubmit={handleSearch} className="search-form">
                <input
                    type="text"
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                    placeholder="Digite sua pesquisa"
                    className="search-form__input"
                />
                <button type="submit" className="search-form__button">
                    Buscar
                </button>
            </form>

            {loading && <p className="message">Carregando...</p>}
            {error && <p className="message message--error">{error}</p>}

            {/* Lista de Vídeos */}
            <div className="video-list">
                {videos.length > 0 ? (
                    videos.map((video) => (
                        <Video
                            key={video.id.videoId}
                            videoId={video.id.videoId}
                            titulo={video.snippet.title}
                            canal={video.snippet.channelTitle}
                            thumbnail={video.snippet.thumbnails.medium.url}
                        />
                    ))
                ) : (
                    <p className="message">Nenhum vídeo encontrado.</p>
                )}
            </div>
        </div>
    );
}
