import React, { useState, useEffect } from 'react';
import Video from './Video';
import { searchVideos } from './VideoList';
import styles from './modulescss'
export default function VideoPage() {
    const [videos, setVideos] = useState([]);
    const [query, setQuery] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const fetchVideos = async (searchQuery) => {
        setLoading(true);
        setError('');
        try {
            const results = await searchVideos(searchQuery);
            setVideos(results);
        } catch (err) {
            setError('Erro ao carregar vídeos.');
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchVideos('Como investir em ações imobiliárias');
    }, []);

    const handleSearch = (event) => {
        event.preventDefault();
        if (query.trim()) {
            fetchVideos(query);
        }
    };

    return (
        <div className={styles.videoPage}>
            <h1 className={styles.videoPageTitle}>Vídeos Recomendados</h1>

            {/* Formulário de Busca */}
            <form className={styles.searchForm} onSubmit={handleSearch}>
                <input
                    type="text"
                    className={styles.searchInput}
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                    placeholder="Digite sua pesquisa"
                />
                <button type="submit" className={styles.searchButton}>Buscar</button>
            </form>

            {loading && <p className={styles.loadingMessage}>Carregando...</p>}
            {error && <p className={styles.errorMessage}>{error}</p>}

            <div className={styles.videoList}>
                {videos.length > 0 ? (
                    videos.map((video) => (
                        <Video
                            key={video.id.videoId}
                            videoId={video.id.videoId}
                            titulo={video.snippet.title}
                            canal={video.snippet.channelTitle}
                            thumbnail={video.snippet.thumbnails.medium.url}
                        />
                    ))
                ) : (
                    <p className={styles.noVideos}>Nenhum vídeo encontrado.</p>
                )}
            </div>
        </div>
    );
}
