import React, { useState, useEffect } from 'react';
import VideoList from './VideoList';
import { searchVideos } from './VideoList';

// Lista de vídeos destacados
const videosDestacados = [
    {
        id: 'dQw4w9WgXcQ',
        titulo: 'Título do Vídeo Exemplo 1',
        canal: 'Canal Exemplo 1',
        thumbnail: 'https://img.youtube.com/vi/dQw4w9WgXcQ/mqdefault.jpg'
    },
    {
        id: 'Q6x0xnI0uCg',
        titulo: 'Aula para Iniciantes em Investimentos',
        canal: 'Primo Pobre',
        thumbnail: 'https://img.youtube.com/vi/Q6x0xnI0uCg/mqdefault.jpg'
    },
    {
        id: '3EfGhI4JkLm',
        titulo: 'Título do Vídeo Exemplo 3',
        canal: 'Canal Exemplo 3',
        thumbnail: 'https://img.youtube.com/vi/3EfGhI4JkLm/mqdefault.jpg'
    }
];

export default function VideoPage() {
    const [videos, setVideos] = useState([]);
    const [query, setQuery] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [isSearching, setIsSearching] = useState(false);

    // Função para buscar vídeos na API do YouTube
    const handleSearch = async (searchQuery) => {
        setLoading(true);
        setError('');
        setIsSearching(true);
        try {
            const results = await searchVideos(searchQuery);
            setVideos(results);
        } catch (err) {
            setError('Erro ao buscar vídeos.');
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    // Mostrar vídeos destacados inicialmente
    useEffect(() => {
        setVideos(videosDestacados);
    }, []);

    // Função chamada ao submeter o formulário de busca
    const handleSubmit = (event) => {
        event.preventDefault();
        if (query.trim()) {
            handleSearch(query);
        }
    };

    return (
        <div>
            <h1>Plataforma de Vídeos</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                    placeholder="Buscar vídeos no YouTube"
                />
                <button type="submit">Buscar</button>
            </form>

            {loading && <p>Carregando...</p>}
            {error && <p className="error">{error}</p>}

            <h2>{isSearching ? 'Resultados da Pesquisa' : 'Vídeos Destacados'}</h2>
            <VideoList videos={videos} />
        </div>
    );
}
