import React, { useState } from 'react';
import emailjs from '@emailjs/browser';


function EmailForm() {
  const [email, setEmail] = useState('budasfecap@gmail.com');
  const [message, setMessage] = useState('');
  const [status, setStatus] = useState('');

  const handleSendEmail = (e) => {
    e.preventDefault();

    if (message.trim() === '') {
      setStatus('Por favor, escreva uma mensagem.');
      return;
    }

    const templateParams = {
      to_email: email,
      message: message,
    };

    emailjs.send(
      'service_q3265j5',   // Substitua pelo seu Service ID
      'SEU_TEMPLATE_ID',  // Substitua pelo seu Template ID
      templateParams,
      'SEU_USER_ID'       // Substitua pelo seu User ID
    )
      .then(() => {
        setStatus('E-mail enviado com sucesso!');
        setMessage('');
      })
      .catch((error) => {
        console.error('Erro ao enviar e-mail:', error);
        setStatus('Ocorreu um erro ao enviar o e-mail.');
      });
  };

  return (
    <div className="email-form-container">
      <h2>Envie uma mensagem</h2>
      <form onSubmit={handleSendEmail}>
        <label>Selecione o destinat치rio:</label>
        <select value={email} onChange={(e) => setEmail(e.target.value)}>
          <option value="email1@example.com">Destinat치rio 1</option>
          <option value="email2@example.com">Destinat치rio 2</option>
          <option value="email3@example.com">Destinat치rio 3</option>
        </select>

        <label>Sua mensagem:</label>
        <textarea
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Digite sua mensagem aqui"
        />

        <button type="submit">Enviar</button>
      </form>
      {status && <p className="status">{status}</p>}
    </div>
  );
}

export default EmailForm;
